import { Form, Input, DatePicker, TimePicker, Button, message } from 'antd';
import dayjs from 'dayjs';
import React, { useState } from 'react';
import '../content.css';

const { TextArea } = Input;

const Content = () => {

    const [form] = Form.useForm();
    const [isSubmitting, setIsSubmitting] = useState(false);

    const onFinish = (values) => {
        setIsSubmitting(true);
        message.success('Inquiry submitted successfully');

        // Log the inquiry details in the console for testing purposes
        console.log('Inquiry Details: ', values);

        // Clear the form after submission
        form.resetFields();
        setIsSubmitting(false);
    };

    const validateDate = (rule, value, callback) => {
        if (value && value < dayjs().startOf('day')) {
            callback('Invalid date');
        } else {
            callback();
        }
    };

    const validateTime = (rule, value, callback) => {
        if (value && value < dayjs().startOf('hour')) {
            callback('Invalid time');
        } else {
            callback();
        }
    };

    return (
        <>
            {/* <div>
                <img src="./taxi.gif" alt="loading..." />
            </div> */}
            <div className='form'>
                <Form
                    form={form}
                    name="inquiry"
                    onFinish={onFinish}
                    layout="vertical"
                >
                    <Form.Item
                        label="Pickup Location"
                        name="pickup"
                        rules={[
                            {
                                required: true,
                                message: 'Please enter pickup location',
                            },
                        ]}
                    >
                        <Input placeholder="Enter pickup location" />
                    </Form.Item>

                    <Form.Item
                        label="Drop-off Location"
                        name="dropoff"
                        rules={[
                            {
                                required: true,
                                message: 'Please enter drop-off location',
                            },
                        ]}
                    >
                        <Input placeholder="Enter drop-off location" />
                    </Form.Item>

                    <Form.Item
                        label="Date"
                        name="date"
                        rules={[
                            {
                                required: true,
                                message: 'Please select a date',
                            },
                            {
                                validator: validateDate,
                            },
                        ]}
                    >
                        <DatePicker />
                    </Form.Item>

                    <Form.Item
                        label="Time"
                        name="time"
                        rules={[
                            {
                                required: true,
                                message: 'Please select a time',
                            },
                            {
                                validator: validateTime,
                            },
                        ]}
                    >
                        <TimePicker format="h:mm a" />
                    </Form.Item>

                    <Form.Item
                        label="Additional Requirements"
                        name="requirements"
                    >
                        <TextArea rows={4} placeholder="Enter additional requirements" />
                    </Form.Item>

                    <Form.Item>
                        <Button type="primary" htmlType="submit" loading={isSubmitting}>
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </>
    );
};

export default Content;
